package centralLibrary;

import centralLibrary.scenes.BerandaScene;
import javafx.application.Application;
import javafx.scene.image.Image;
import javafx.stage.Stage;

public class App extends Application {
    private Stage stage;

    public Stage getStage() {
        return stage;
    }

    public void setStage(Stage stage) {
        this.stage = stage;
    }

    public static void main(String[] args) {
        launch(args);
    }

    @Override
    public void start(Stage stage) throws Exception {
        BerandaScene berandaScene =  new BerandaScene(stage);
        stage.setResizable(false);
        Image logo = new Image("images/logoPerpus.png");
        stage.getIcons().add(logo);
        stage.setTitle("Central Library");
        berandaScene.berandaScene();
    }
}